
public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=5;//declaración e iniciación e una línea
		
		int b;//declaración e iniciación en diferentes líneas
		
		b=7;
		
		int c=b+a; //operador suma
		
		c++;//incrementa en 1 el valor de c
		
		c+=6; //incrementa en 6 el valor de c
		
		c-=2; //decrementa en 2 el valor de c
		
		
		
		System.out.println(c);
		
		
		
	}

}
